import '../css/login.css';
import React, {useState} from "react"
import AppRouter from '../AppRouter';


function Login(){

  const [emaill,setEmail]= useState("");
  const [senhaa,setSenha]= useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("submit",emaill,senhaa)
  }

    return (
               <div className="container">
                  <h1>Login</h1>

                      <form className="login-form" onSubmit={handleSubmit} >
                        <div className="login-form-group">
                          <input name="email" className="form-field" placeholder="Email" value={emaill} onChange={(e)=> setEmail(e.target.value)} />


                        </div>
                        {/*Outro campo*/}
                        <div className="form-group">
                          <input name="password" type="password" className="form-field" placeholder="Senha" value={senhaa} onChange={(e)=> setSenha(e.target.value)}/>


                        </div>

                        <button className="button" type="submit">
                          Login
                        </button>
                      </form>

                </div>

      );
}

export default Login;
