            id: "123",
            email
        }
       localStorage.setItem("user",JSON.stringify(loggedUser))

        if( senha === "123")
        {
            setUser(loggedUser)
            navigate("/")
        }



    }



    const logout =() =>{
        console.log("to fora")
    }


return(
    <AuthContext.Provider value={{authenticated:
