import './css/login.css';
import React, {useState} from "react"
import * as yup from "yup";
import { ErrorMessage, Formik, Form, Field } from "formik";


function Login(){

  const [email,setEmail]= useState("");
  const [senha,setSenha]= useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("submit",email,senha)
  }

  const validationsLogin = yup.object().shape({
    email: yup
      .string()
      .email("email inválido")
      .required("O email é obrigatório"),
    password: yup
      .string()
      .min(4, "A senha deve ter pelo menos 8 caracteres")
      .required("A senha é obrigatória"),
  });

    return (

        <div>
               <div className="container">
      <h1>Login</h1>
      <Formik
        initialValues={{}}
        validationSchema={validationsLogin}

      >
        <Form className="login-form" onSubmit={handleSubmit}>
          <div className="login-form-group">
            <Field name="email" className="form-field" placeholder="Email" value={email} onChange={(e)=> setEmail(e.target.value)} />

            <ErrorMessage
              component="span"
              name="email"
              className="form-error"
            />
          </div>
          {/*Outro campo*/}
          <div className="form-group">
            <Field name="password" type="password" className="form-field" placeholder="Senha" value={senha} onChange={(e)=> setSenha(e.target.value)}/>

            <ErrorMessage
              component="span"
              name="password"
              className="form-error"
            />
          </div>

          <button className="button" type="submit">
            Login
          </button>
        </Form>
      </Formik>
      </div>
        </div>
      );
}

export default Login;
